"use client"

import { useState } from "react"
import axios from "axios"
import "./CreatePost.css"

const CreatePost = ({ user, onPostCreated }) => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [imagePreview, setImagePreview] = useState("")
  const [loading, setLoading] = useState(false)
  const [uploadError, setUploadError] = useState("")

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (!file) return

    console.log("Selected file:", {
      name: file.name,
      type: file.type,
      size: file.size,
      lastModified: file.lastModified,
    })

    // Check file type
    const validTypes = ["image/jpeg", "image/jpg", "image/png"]
    const fileExtension = file.name.split(".").pop().toLowerCase()
    const validExtensions = ["jpg", "jpeg", "png"]

    if (!validTypes.includes(file.type) && !validExtensions.includes(fileExtension)) {
      setUploadError(`Invalid file type. Please select a JPG, JPEG, or PNG file. Got: ${file.type}`)
      return
    }

    // Check file size (5MB max)
    if (file.size > 5 * 1024 * 1024) {
      setUploadError("File is too large. Maximum size is 5MB.")
      return
    }

    setUploadError("")
    setImage(file)

    // Create preview
    const reader = new FileReader()
    reader.onloadend = () => {
      setImagePreview(reader.result)
    }
    reader.onerror = () => {
      setUploadError("Failed to read file")
    }
    reader.readAsDataURL(file)
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!content.trim() && !image) return

    setLoading(true)
    setUploadError("")

    try {
      let imageUrl = ""

      // Upload image if present
      if (image) {
        console.log("Uploading image:", image.name)

        const formData = new FormData()
        formData.append("image", image)

        try {
          const uploadResponse = await axios.post("http://localhost:5000/api/upload", formData, {
            headers: {
              "Content-Type": "multipart/form-data",
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          })

          console.log("Upload response:", uploadResponse.data)
          imageUrl = `http://localhost:5000${uploadResponse.data.imageUrl}`
        } catch (uploadError) {
          console.error("Image upload failed:", uploadError)
          const errorMsg = uploadError.response?.data?.error || uploadError.message
          setUploadError(`Image upload failed: ${errorMsg}`)
          setLoading(false)
          return
        }
      }

      // Create post
      const postResponse = await axios.post(
        "http://localhost:5000/api/posts",
        {
          content,
          image: imageUrl,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        },
      )

      console.log("Post created:", postResponse.data)

      // Reset form
      setContent("")
      setImage(null)
      setImagePreview("")
      setUploadError("")

      // Notify parent component
      onPostCreated()
    } catch (error) {
      console.error("Error creating post:", error)
      setUploadError("Failed to create post: " + (error.response?.data?.error || error.message))
    } finally {
      setLoading(false)
    }
  }

  const removeImage = () => {
    setImage(null)
    setImagePreview("")
    setUploadError("")
  }

  return (
    <div className="create-post">
      <div className="create-post-header">
        <div className="avatar">
          {user.avatar ? (
            <img src={user.avatar || "/placeholder.svg"} alt={user.username} />
          ) : (
            <div className="avatar-placeholder">{user.username.charAt(0).toUpperCase()}</div>
          )}
        </div>
        <h3>Create a Post</h3>
      </div>

      <form onSubmit={handleSubmit}>
        <textarea
          placeholder={`What's on your mind, ${user.username}?`}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="3"
        />

        {uploadError && <div className="error-message">{uploadError}</div>}

        {imagePreview && (
          <div className="image-preview">
            <img src={imagePreview || "/placeholder.svg"} alt="Preview" />
            <button type="button" onClick={removeImage} className="remove-image">
              âœ•
            </button>
          </div>
        )}

        <div className="post-actions">
          <label className="image-upload">
            <input type="file" accept=".jpg,.jpeg,.png" onChange={handleImageChange} style={{ display: "none" }} />ðŸ“·
            Photo (JPG, JPEG, PNG)
          </label>

          <button type="submit" disabled={loading || (!content.trim() && !image)} className="post-button">
            {loading ? "Posting..." : "Post"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default CreatePost
