"use client"

import { useState, useEffect } from "react"
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom"
import axios from "axios"
import "./App.css"

// Components
import Navbar from "./components/Navbar"
import Login from "./components/Login"
import Register from "./components/Register"
import Home from "./components/Home"
import Profile from "./components/Profile"
import CreatePost from "./components/CreatePost"

// Set up axios defaults
axios.defaults.baseURL = "http://localhost:5000/api"

function App() {
  const [user, setUser] = useState(null)
  const [loading, setLoading] = useState(true)

  const setupAxiosInterceptors = () => {
    axios.interceptors.request.use(
      (config) => {
        const token = localStorage.getItem("token")
        if (token) {
          config.headers.Authorization = `Bearer ${token}`
        }
        return config
      },
      (error) => {
        return Promise.reject(error)
      },
    )

    // Add response interceptor to handle token expiration
    axios.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          // Token is invalid or expired
          console.log("Token expired or invalid, logging out...")
          logout()
        }
        return Promise.reject(error)
      },
    )
  }

  const verifyToken = async () => {
    const token = localStorage.getItem("token")
    if (!token) {
      setLoading(false)
      return
    }

    try {
      // Verify token by making a request to a protected endpoint
      const response = await axios.get("/posts", {
        headers: { Authorization: `Bearer ${token}` },
      })

      // If successful, token is valid
      const userData = localStorage.getItem("user")
      if (userData) {
        setUser(JSON.parse(userData))
      }
    } catch (error) {
      console.log("Token verification failed:", error.response?.status)
      // Token is invalid, clear storage
      localStorage.removeItem("token")
      localStorage.removeItem("user")
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    setupAxiosInterceptors()
    verifyToken() // Verify token on app start
  }, [])

  const login = (userData, token) => {
    localStorage.setItem("token", token)
    localStorage.setItem("user", JSON.stringify(userData))
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`
    setUser(userData)
  }

  const logout = () => {
    localStorage.removeItem("token")
    localStorage.removeItem("user")
    delete axios.defaults.headers.common["Authorization"]
    setUser(null)
    console.log("User logged out")
  }

  // Add function to clear all data (for development/testing)
  const clearAllData = () => {
    localStorage.clear()
    sessionStorage.clear()
    setUser(null)
    delete axios.defaults.headers.common["Authorization"]
    console.log("All data cleared")
  }

  // Add keyboard shortcut for clearing data (Ctrl+Shift+C)
  useEffect(() => {
    const handleKeyPress = (e) => {
      if (e.ctrlKey && e.shiftKey && e.key === "C") {
        clearAllData()
      }
    }
    window.addEventListener("keydown", handleKeyPress)
    return () => window.removeEventListener("keydown", handleKeyPress)
  }, [])

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Verifying login...</p>
      </div>
    )
  }

  return (
    <Router>
      <div className="App">
        {user && <Navbar user={user} onLogout={logout} />}

        {/* Development helper - remove in production */}
        {process.env.NODE_ENV === "development" && (
          <div
            style={{
              position: "fixed",
              top: "10px",
              right: "10px",
              zIndex: 1000,
              background: "#ff4444",
              color: "white",
              padding: "5px 10px",
              borderRadius: "4px",
              fontSize: "12px",
              cursor: "pointer",
            }}
            onClick={clearAllData}
            title="Click to clear all data (or press Ctrl+Shift+C)"
          >
            Clear Data
          </div>
        )}

        <Routes>
          <Route path="/login" element={!user ? <Login onLogin={login} /> : <Navigate to="/" />} />
          <Route path="/register" element={!user ? <Register onLogin={login} /> : <Navigate to="/" />} />
          <Route path="/" element={user ? <Home user={user} /> : <Navigate to="/login" />} />
          <Route path="/profile/:userId" element={user ? <Profile currentUser={user} /> : <Navigate to="/login" />} />
          <Route path="/create-post" element={user ? <CreatePost user={user} /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  )
}

export default App
