"use client"

import { useState } from "react"
import axios from "axios"
import "./CreatePost.css"

const CreatePost = ({ user, onPostCreated }) => {
  const [content, setContent] = useState("")
  const [image, setImage] = useState(null)
  const [imagePreview, setImagePreview] = useState("")
  const [loading, setLoading] = useState(false)

  const handleImageChange = (e) => {
    const file = e.target.files[0]
    if (file) {
      setImage(file)
      const reader = new FileReader()
      reader.onloadend = () => {
        setImagePreview(reader.result)
      }
      reader.readAsDataURL(file)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()
    if (!content.trim() && !image) return

    setLoading(true)

    try {
      let imageUrl = ""

      // Upload image if present
      if (image) {
        const formData = new FormData()
        formData.append("image", image)

        const uploadResponse = await axios.post("/upload", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        })
        imageUrl = uploadResponse.data.imageUrl
      }

      // Create post
      await axios.post("/posts", {
        content,
        image: imageUrl,
      })

      // Reset form
      setContent("")
      setImage(null)
      setImagePreview("")

      // Notify parent component
      onPostCreated()
    } catch (error) {
      console.error("Error creating post:", error)
      alert("Failed to create post")
    } finally {
      setLoading(false)
    }
  }

  const removeImage = () => {
    setImage(null)
    setImagePreview("")
  }

  return (
    <div className="create-post">
      <div className="create-post-header">
        <div className="avatar">
          {user.avatar ? (
            <img src={user.avatar || "/placeholder.svg"} alt={user.username} />
          ) : (
            <div className="avatar-placeholder">{user.username.charAt(0).toUpperCase()}</div>
          )}
        </div>
        <h3>Create a Post</h3>
      </div>

      <form onSubmit={handleSubmit}>
        <textarea
          placeholder={`What's on your mind, ${user.username}?`}
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="3"
        />

        {imagePreview && (
          <div className="image-preview">
            <img src={imagePreview || "/placeholder.svg"} alt="Preview" />
            <button type="button" onClick={removeImage} className="remove-image">
              âœ•
            </button>
          </div>
        )}

        <div className="post-actions">
          <label className="image-upload">
            <input type="file" accept="image/*" onChange={handleImageChange} style={{ display: "none" }} />ðŸ“· Photo
          </label>

          <button type="submit" disabled={loading || (!content.trim() && !image)} className="post-button">
            {loading ? "Posting..." : "Post"}
          </button>
        </div>
      </form>
    </div>
  )
}

export default CreatePost
